import React, { Component } from 'react';
import { Fragment } from 'react';
import Toper2 from './Toper2';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput,MDBCard, MDBCardBody } from 'mdbreact'; 
class Verifymn extends Component {
    constructor(props) {
        super(props);
        
        this.state = {
            gmail : '',
            phonenumber : ''
        }
    }
    handlenumberchange = (event) => {
        this.setState({
            phonenumber : event.target.value
        })
    }  
    handlegmailchange = (event) => {
        this.setState({
            gmail : event.target.value
        })
    }      
    handlesubmit = (event) => {
        
        alert(`${this.state.gmail} ${this.state.phonenumber} `)
        axios.post('http://127.0.0.1:8000/api/',{
            firstname : this.state.firstname,
            lastname : this.state.lastname,
            gender : this.state.gender,
            date : this.state.date,
            password : this.state.password,
            phonenumber : this.state.phonenumber,
            gmail : this.state.gmail

        });
        //this.props.history.push({pathname : `/verifymn`, state : {details : this.state}
    //});
    }    
    render() {
        return (
            <div>
                <Toper2/>
                <br/>
                <MDBContainer>
                <MDBRow>
                <MDBCol md="8" className="mb-4"  > {/*style={{ maxWidth: "22rem" }}*/}
                <MDBCard color="indigo" text="white" className="text-center" align="center" style={{ maxWidth: "22rem" }}>
                    <MDBCardBody align = "center" style={{ maxWidth: "22rem" }}>
                            You are One Step Ahead For Registration
                    </MDBCardBody>
                </MDBCard> 
                </MDBCol>   
                </MDBRow>                  
                <form
                className="needs-validation"
                onSubmit={this.handlesubmit}
                noValidate
                >
                {/*<div>
                <label> Mobile Number </label>
                <input type = "text" value = {this.state.phonenumber} onChange={this.handlenumberchange} />
                <br />
                </div>  */}
                {/*<div>
                <label> Email </label>
                <input type = "text" value = {this.state.gmail} onChange={this.handlegmailchange} />
                <br />
                </div>*/} 
                <MDBRow>
                <MDBCol md="8" className="mb-4"  > 
                <MDBInput  style={{ maxWidth: "26rem" }} value = {this.state.phonenumber} onChange={this.handlenumberchange}
                label="Enter Mobile NUmber"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                required
              />                                           
              <MDBInput style={{ maxWidth: "26rem" }}
                label="Your email"
                icon="envelope"
                group
                type="email"
                validate
                error="wrong"
                success="right"
                required
              />
              <MDBInput  style={{ maxWidth: "26rem" }} value = {this.state.gmail} onChange={this.handlegmailchange}
                label="Confirm your email"
                icon="exclamation-triangle"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                required
              />  
                <small id="emailHelp" className="form-text text-muted">
                We'll never share your email with anyone else.
              </small>                 
                </MDBCol>   
            </MDBRow>                   
            <MDBRow>  
                    <MDBCol md="4" className="mb-4">                   
                    <Fragment>

                    <MDBBtn gradient="blue" type="submit">Submit</MDBBtn>
                    </Fragment>  
                    </MDBCol>
                </MDBRow>                                       
                </form>

            </MDBContainer>
            </div>
        );
    }
}

export default Verifymn;
