import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput,MDBCard, MDBCardBody } from 'mdbreact'; 

import React, { Component } from 'react';
import axios from 'axios';
import Toper2 from "./Toper2";


class Signup extends Component {
    constructor(props) {
        super(props);

        this.state = {
            firstname : '',
            lastname : '',
            gender : '',
            date : '',
            password : ''
        }
        
    }
    
    handlefirstnamechange = (event) => {
        this.setState({
            firstname : event.target.value
        })
    }
    handlelastnamechange = (event) => {
        this.setState({
            lastname : event.target.value
        })
    } 
    handlegenderchange = (event) => {
        this.setState({
            gender : event.target.value,
           
        })
    }         
    handlepasswordchange = (event) => {
        this.setState({
            password : event.target.value,
           
        })
    }           
    handlesubmit = (event) => {
        
        alert(`${this.state.firstname} ${this.state.lastname} ${this.state.gender} ${this.state.date} ${this.state.password}`)
        axios.post('http://127.0.0.1:8000/api/',{
            firstname : this.state.firstname,
            lastname : this.state.lastname,
            gender : this.state.gender,
            date : this.state.date,
            password : this.state.password
        });
        this.props.history.push({pathname : `/verifymn`, state : {details : this.state}
    });
    }
    render() {

        return (
            <div>
        <Toper2/>
        <br />
        <MDBContainer>
            <MDBRow>
                <MDBCol md="6" className="mb-4"  style={{ maxWidth: "22rem" }}> {/*style={{ maxWidth: "22rem" }}*/}
                <MDBCard color="indigo" text="white" className="text-center" align="center">
                    <MDBCardBody align = "center">
                            SIGN UP
                    </MDBCardBody>
                </MDBCard>    
                <form onSubmit = {this.handlesubmit}>
                {/*<div>
                <label> FirstName </label>
                <input type = "text" value = {this.state.firstname} onChange={this.handlefirstnamechange} />
                <br />
                </div>*/}
                <MDBInput
                label="Your name"
               
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.firstname} onChange={this.handlefirstnamechange}
                style={{ maxWidth: "20rem" }}
                icon="user"
                />
                <div>
                <label> LastName </label>
                <input type = "text" value = {this.state.lastname} onChange={this.handlelastnamechange} /> 
                <br />
                </div>
                <div>
                <label> Gender </label>
                <select  value = {this.state.gender}  onChange={this.handlegenderchange}>
                    <option value = "" >  </option>
                    <option value = "male" > Male </option>
                    <option value = "female"> Female </option>
                </select>
                </div>
                <div>
                <label> DateofBirth </label>    
                <input type = "date" onChange = {event => this.setState({date:event.target.value})}  />  
                </div>    
                <div>
                <label> Password </label>    
                <input type = "password" value = {this.state.password} onChange = {this.handlepasswordchange}  />  
                </div>                  
                <div>
                    
                    <button type = "submit">Submit</button>
                     
                </div>   
                
        </form>  
        </MDBCol>
        </MDBRow>
        </MDBContainer>       
            </div>
        );
    }
}

export default Signup;
