import React, { Component } from 'react';
import { Button } from 'antd';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput,MDBCard, MDBCardBody } from 'mdbreact'; 
import { Fragment } from 'react';
import CustomLayout from '../containers/CustomLayout';
import Toper3 from './Toper3';
import { List, Avatar, Icon } from 'antd';
import logoinr from './logoinr.jpg';

const listData = [];


const IconText = ({ type, text }) => (
  <span>
    <Icon type={type} style={{ marginRight: 8 }} />
    {text}
  </span>
);
class FindList extends Component {

    constructor(props) {
        super(props);
        
        this.state = {
            email :'',
            source : '',
            destination : '',
            timeofleave : '',
            dateofleave : '',
            price : '',
            seats : '',
            anything : ''            
        }
        }    
    componentDidMount(){
        
        console.log("test "+ this.props.location.state.info);
        for (let i = 0; i < this.props.location.state.info.length; i++) {
            listData.push({
              href: 'http://ant.design',
              title: `Offer ${i+1}`,
              avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
              description: `price : ${this.props.location.state.info[i].price}   seats : ${this.props.location.state.info[i].seats}`,      
              from:
                `${this.props.location.state.info[i].source} `,
               to : 
                `${this.props.location.state.info[i].destination}`,
              email : 
              `${this.props.location.state.info[i].email}`,
            });
          }
    }    
    handleBooking = () => {
        //alert("set for offering")
        //console.log("info");
      }; 
    render() {
        return (
            <div>
                <Toper3>{this.props.location.state.details.firstname} {this.props.location.state.details.lastname} </Toper3>
                <CustomLayout>
                <List
                itemLayout="vertical"
                size="large"
                pagination={{
                onChange: page => {
                    console.log(page);
                },
                pageSize: 3,
                }}
                dataSource={listData}
                footer={
                <div>
                    <b>ant design</b> footer part
                </div>
                }
                renderItem={item => (
                <List.Item
                    key={item.title}
                    actions={[
                    <IconText type="star-o" text="156" key="list-vertical-star-o" />,
                    <IconText type="like-o" text="156" key="list-vertical-like-o" />,
                    <IconText type="message" text="2" key="list-vertical-message" />,
                    ]}
                    extra={
                    <img
                        width={272}
                        alt="logo"
                        src={logoinr}
                    />
                    }
                >
                    <List.Item.Meta
                    avatar={<Avatar src={item.avatar} />}
                    title={<a href={item.href}>{item.title}</a>}
                    description={item.description}
                    />
                    <label> FROM  :</label>
                    {item.from}
                    <br/>
                    <label> TO  :</label>                    
                    {item.to}
                    <br/>
                    <label> EMAIL  :</label>                    
                    {item.email}
                    <br />                     
                    {/* <MDBRow>  
                    <MDBCol md="6" className="mb-4">                   
                    <Fragment>

                    <MDBBtn gradient="blue" type="submit">Submit</MDBBtn>
                    </Fragment>  
                    </MDBCol>
                </MDBRow>                */}
                </List.Item>
                )}
            />                    
                </CustomLayout >    
            </div>
        );
    }
}

export default FindList;
