import React, { Component } from 'react';
import { Button } from 'antd';

import CustomLayout from '../containers/CustomLayout';
import Toper3 from './Toper3';
import { List, Avatar, Icon } from 'antd';
import logoinr from './logoinr.jpg';
import axios from 'axios';
const listData = [];

const IconText = ({ type, text }) => (
  <span>
    <Icon type={type} style={{ marginRight: 8 }} />
    {text}
  </span>
);

class Notifications extends Component
{
    constructor(props) {
        super(props);
    
    this.state = {
        info : [],
        firstname : '',
        lastname : '',
        gender : '',
        date : '',
        phonenumber : '',
        email : '',
    }
    }
    refine(){
        //alert("refinement");
        const result = this.state.info.filter(word => word.offer_email == this.state.email);
        localStorage.setItem('length',result.length);


    }    
    componentDidMount(){
        //const test = localStorage.getItem('test');
        //console.log(test);
        const res = JSON.parse(localStorage.getItem('details'));
        this.setState({firstname:res.firstname});
        this.setState({lastname:res.lastname});
        this.setState({gender:res.gender});
        this.setState({phonenumber:res.phonenumber});
        this.setState({email:res.email});

        axios.get('http://127.0.0.1:8000/api4/')
        .then(res=>{
            this.setState({
                info : res.data
            });
            //console.log(res.data);
            this.refine();

        })
        const r = localStorage.getItem('length');
        //console.log("check "+);
        for (let i = 0; i < 2; i++) {
            
            listData.push({
              href: 'http://ant.design',
              title: `ant design part ${i}`,
              avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
              description:
                'Ant Design, a design language for background applications, is refined by Ant UED Team.',
              content:
                'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
            });
          
      }               
    }            
        //console.log(res.firstname);
        //console.log(JSON.stringify(this.state.info));
    render() {
        return (
            <div>
                <Toper3> {this.state.firstname} {this.state.lastname} </Toper3>
                <CustomLayout>
                <List
                    itemLayout="vertical"
                    size="large"
                    pagination={{
                    onChange: page => {
                        console.log(page);
                    },
                    pageSize: 3,
                    }}
                    dataSource={listData}
                    footer={
                    <div>
                        <b>ant design</b> footer part
                    </div>
                    }
                    renderItem={item => (
                    <List.Item
                        key={item.title}
                        actions={[
                        <IconText type="star-o" text="156" key="list-vertical-star-o" />,
                        <IconText type="like-o" text="156" key="list-vertical-like-o" />,
                        <IconText type="message" text="2" key="list-vertical-message" />,
                        ]}
                        extra={
                        <img
                            width={272}
                            alt="logo"
                            src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                        />
                        }
                    >
                        <List.Item.Meta
                        avatar={<Avatar src={item.avatar} />}
                        title={<a href={item.href}>{item.title}</a>}
                        description={item.description}
                        />
                        {item.content}
                    </List.Item>
                    )}
                />
                </CustomLayout>
            </div>
        );
    }     
}

export default Notifications;
