import React, { Component } from 'react';
import Toper3 from './Toper3';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput,MDBCard, MDBCardBody } from 'mdbreact'; 
import { Fragment } from 'react';
import axios from 'axios';
import TimeField from 'react-simple-timefield';
import SearchBar from 'material-ui-search-bar';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import Script from 'react-load-script';

class Offer extends Component {
    constructor(props) {
        super(props);
        
        this.state = {
            city : '',
            source : '',
            destination : '',
            date : '',
            time : '',
            ppt : '',
            nos : '',
            ats : ''
        }
        }
        handlesourcechange = source => {
            this.setState({ source });
          };
        handledestinationchange = (event) => {
            this.setState({
                destination : event.target.value
            })
        }    
        handledatechange = (event) => {
            this.setState({
                date : event.target.value
            })
        }   
        handletimechange = (event) => {
            this.setState({
                time : event.target.value
            })
        }   
        handlepptchange = (event) => {
            this.setState({
                ppt : event.target.value
            })
        }   
        handlenoschange = (event) => {
            this.setState({
                nos : event.target.value
            })
        }   
        handleatschange = (event) => {
            this.setState({
                ats : event.target.value
            })
        }          
        handleSubmit = (event) => {
            alert(` ${this.props.location.state.details.email} ${this.state.source}  ${this.state.destination}  ${this.state.date}  ${this.state.time}  ${this.state.ppt} ${this.state.nos} ${this.state.ats} `)
            axios.post('http://127.0.0.1:8000/api2/',{
                email : this.props.location.state.details.email,
                source : this.state.source,
                destination : this.state.destination,
                timeofleave : this.state.time,
                dateofleave : this.state.date,
                price : this.state.ppt,
                seats : this.state.nos,
                anything : this.state.ats
    
            });  
            this.props.history.push('/home');          
        }                                             
        handleScriptLoad = () => {
            // Declare Options For Autocomplete
            const options = {
              types: ['(cities)'],
            };
        
            // Initialize Google Autocomplete
            /*global google*/ // To disable any eslint 'google not defined' errors
            this.autocomplete = new google.maps.places.Autocomplete(
              document.getElementById('autocomplete'),
              options,
            );
        
            // Avoid paying for data that you don't need by restricting the set of
            // place fields that are returned to just the address components and formatted
            // address.
            this.autocomplete.setFields(['address_components', 'formatted_address']);
        
            // Fire Event when a suggested name is selected
            this.autocomplete.addListener('place_changed', this.handlePlaceSelect);
          }
          
          handlePlaceSelect = () => {
        
            // Extract City From Address Object
            const addressObject = this.autocomplete.getPlace();
            const address = addressObject.address_components;
        
            // Check if address is valid
            if (address) {
              // Set State
              this.setState(
                {
                  city: address[0].long_name,
                  source: addressObject.formatted_address,
                }
              );
            }
          }        
    render() {
        return (
            <div>
                {/* <Toper3 >{this.props.location.state.details.firstname} {this.props.location.state.details.lastname}</Toper3> */}
                <br />
             <MDBContainer>
                <MDBRow>
                <MDBCol md="6" className="mb-4"  > {/*style={{ maxWidth: "22rem" }}*/}
                <MDBCard color="indigo" text="white" className="text-center" align="center" style={{ maxWidth: "22rem" }}>
                    <MDBCardBody align = "center" >
                            FILL IN THE DETAILS
                    </MDBCardBody>
                </MDBCard> 
                </MDBCol>   
                </MDBRow>   
                <form onSubmit={this.handleSubmit}>

                <MDBRow>
                    <MDBCol md="6" className="mb-4">
                    <MuiThemeProvider>
                    <Script
                    url="https://maps.googleapis.com/maps/api/js?key=your_api_key&libraries=places"
                    onLoad={this.handleScriptLoad}
                    />
                    <SearchBar id="autocomplete" placeholder="" hintText="Search City" value={this.state.source}
                    style={{
                        margin: '0 auto',
                        maxWidth: 800,
                    }}
                    />  
                    </MuiThemeProvider>                       
                {/* <MDBInput
                label="SOURCE"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.source} onChange={this.handlesourcechange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                /> */}
                </MDBCol>
                </MDBRow>

                 <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="DESTINATION"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.destination} onChange={this.handledestinationchange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow>          
                 <MDBRow>
                <MDBCol md="4" className="mb-4">
                <label> TimeofJourney </label>  
                <TimeField
                    value = {this.state.time} onChange={this.handletimechange}
                    //value={time}                     // {String}   required, format '00:00' or '00:00:00'
                    //onChange={(value) => {...}}      // {Function} required
                    input={<input type="text" />} // {Element}  default: <input type="text" />
                    colon=":"                        // {String}   default: ":"
                    showSeconds                      // {Boolean}  default: false
                />                                 
                {/* <input
                type="time"
                step="1"
                value = {this.state.time} onChange={this.handletimechange}
                //value={this.state.time}
                className="form-control"
                placeholder="Time"
               // onChange={(ev) => {this.setState({time:ev.target.value})}
                /> */}
                </MDBCol>
                </MDBRow>                  
                <br/>
                <br/>                                                         
                <MDBRow>  
                <MDBCol md="4" className="mb-4">                   
                                
                <label> DateofJourney </label>    
                <input type = "date" value = {this.state.date} onChange={this.handledatechange} />  {/*(onChange = {event => this.setState({date:event.target.value})} required*/}
                </MDBCol>
                </MDBRow> 
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="Price per co traveller"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                
                value = {this.state.ppt} onChange={this.handlepptchange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      

                </MDBCol>
                </MDBRow>
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="Number of Seats"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.nos} onChange={this.handlenoschange}
                //value = {this.state.lastname} onChange={this.handlelastnamechange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow> 
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="anything to say"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.ats} onChange={this.handleatschange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow>                                                              
                    <MDBRow>  
                    <MDBCol md="6" className="mb-4">                   
                    <Fragment>

                    <MDBBtn gradient="blue" type="submit">Submit</MDBBtn>
                    </Fragment>  
                    </MDBCol>
                </MDBRow>                        
                
        </form>  
        
        
        </MDBContainer>                       
            </div>
        );
    }
}

export default Offer;
