import React, { Component } from 'react';
import Toper3 from './Toper3';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput,MDBCard, MDBCardBody } from 'mdbreact'; 
import { Fragment } from 'react';

class Offer extends Component {
    constructor(props) {
        super(props);
        
        this.state = {
            source : '',
            destination : '',
            date : '',
            time : '',
            ppt : '',
            nos : '',
            ats : ''
        }
        }
        handlesourcechange = (event) => {
            this.setState({
                source : event.target.value
            })
        } 
        handledestinationchange = (event) => {
            this.setState({
                destination : event.target.value
            })
        }    
        handledatechange = (event) => {
            this.setState({
                date : event.target.value
            })
        }   
        handletimechange = (event) => {
            this.setState({
                time : event.target.value
            })
        }   
        handlepptchange = (event) => {
            this.setState({
                ppt : event.target.value
            })
        }   
        handlenoschange = (event) => {
            this.setState({
                nos : event.target.value
            })
        }   
        handleatschange = (event) => {
            this.setState({
                ats : event.target.value
            })
        }                                                  
        
    render() {
        return (
            <div>
                <Toper3/>
                <br />
             <MDBContainer>
                <MDBRow>
                <MDBCol md="6" className="mb-4"  > {/*style={{ maxWidth: "22rem" }}*/}
                <MDBCard color="indigo" text="white" className="text-center" align="center" style={{ maxWidth: "22rem" }}>
                    <MDBCardBody align = "center" >
                            FILL IN THE DETAILS
                    </MDBCardBody>
                </MDBCard> 
                </MDBCol>   
                </MDBRow>   
                <form onSubmit={this.handleSubmit}>

                <MDBRow>
                    <MDBCol md="6" className="mb-4">
                <MDBInput
                label="SOURCE"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.source} onChange={this.handlesourcechange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />
                </MDBCol>
                </MDBRow>

                 <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="DESTINATION"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.destination} onChange={this.handledestinationchange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow>          
                 <MDBRow>
                <MDBCol md="4" className="mb-4">
                <label> TimeofJourney </label>                 
                <input
                type="time"
                step="1"
                value = {this.state.time} onChange={this.handletimechange}
                //value={this.state.time}
                className="form-control"
                placeholder="Time"
               // onChange={(ev) => {this.setState({time:ev.target.value})}
                />
                </MDBCol>
                </MDBRow>                  
                <br/>
                <br/>                                                         
                <MDBRow>  
                <MDBCol md="4" className="mb-4">                   
                                
                <label> DateofJourney </label>    
                <input type = "date" value = {this.state.date} onChange={this.handledatechange} />  {/*(onChange = {event => this.setState({date:event.target.value})} required*/}
                </MDBCol>
                </MDBRow> 
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="Price per co traveller"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                
                value = {this.state.ppt} onChange={this.handlepptchange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      

                </MDBCol>
                </MDBRow>
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="Number of Seats"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.nos} onChange={this.handlenoschange}
                //value = {this.state.lastname} onChange={this.handlelastnamechange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow> 
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="anything to say"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.ats} onChange={this.handleatschange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow>                                                              
                    <MDBRow>  
                    <MDBCol md="6" className="mb-4">                   
                    <Fragment>

                    <MDBBtn gradient="blue" type="submit">Submit</MDBBtn>
                    </Fragment>  
                    </MDBCol>
                </MDBRow>                        
                
        </form>  
        
        
        </MDBContainer>                       
            </div>
        );
    }
}

export default Offer;
