import React, { Component } from 'react';
import Toper3 from './Toper3';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput,MDBCard, MDBCardBody } from 'mdbreact'; 
import { Fragment } from 'react';
import axios from 'axios';
import TimeField from 'react-simple-timefield';
import SearchBar from 'material-ui-search-bar';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import Script from 'react-load-script';
import PlacesAutocomplete, { geocodeByAddress, geocodeByPlaceId, getLatLng } from 'react-places-autocomplete';


const isObject = val => {
    return typeof val === 'object' && val !== null;
  };
  
  const classnames = (...args) => {
    const classes = [];
    args.forEach(arg => {
      if (typeof arg === 'string') {
        classes.push(arg);
      } else if (isObject(arg)) {
        Object.keys(arg).forEach(key => {
          if (arg[key]) {
            classes.push(key);
          }
        });
      } else {
        throw new Error(
          '`classnames` only accepts string or object as arguments'
        );
      }
    });
  
    return classes.join(' ');
  };

class Offer extends Component {
    constructor(props) {
        super(props);
        
        this.state = {
            source: '',
            post_address_obj: {},
            errorMessage: '',
            latitude: null,
            longitude: null,
            isGeocoding: false,   
            d_post_address_obj: {},
            d_errorMessage: '',
            d_latitude: null,
            d_longitude: null,
            d_isGeocoding: false,                      
            destination : '',
            date : '',
            time : '',
            ppt : '',
            nos : '',
            ats : ''
        }
        }
        handleSourceAddressChange = address => {
            // console.log(address);
            this.setState({
              source: address,
              latitude: null,
              longitude: null,
              errorMessage: ""
            });
          };
          handleDestinationAddressChange = address => {
            // console.log(address);
            this.setState({
              destination: address,
              d_latitude: null,
              d_longitude: null,
              d_errorMessage: ""
            });
          }; 
        handledatechange = (event) => {
            this.setState({
                date : event.target.value
            })
        }   
        handletimechange = (event) => {
            this.setState({
                time : event.target.value
            })
        }   
        handlepptchange = (event) => {
            this.setState({
                ppt : event.target.value
            })
        }   
        handlenoschange = (event) => {
            this.setState({
                nos : event.target.value
            })
        }   
        handleatschange = (event) => {
            this.setState({
                ats : event.target.value
            })
        }          
        handleSubmit = (event) => {
            alert(` ${this.props.location.state.details.email} ${this.state.source}  ${this.state.destination}  ${this.state.date}  ${this.state.time}  ${this.state.ppt} ${this.state.nos} ${this.state.ats} `)
            axios.post('http://127.0.0.1:8000/api2/',{
                email : this.props.location.state.details.email,
                source : this.state.source,
                destination : this.state.destination,
                timeofleave : this.state.time,
                dateofleave : this.state.date,
                price : this.state.ppt,
                seats : this.state.nos,
                anything : this.state.ats
    
            });  
            this.props.history.push('/home');          
        }                                             
 
    render() {
        return (
            <div>
                <Toper3 >{this.props.location.state.details.firstname} {this.props.location.state.details.lastname}</Toper3>
                <br />
             <MDBContainer>
                <MDBRow center>
    
                <MDBCol md="6" className="mb-4"  > {/*style={{ maxWidth: "22rem" }}*/}
                <MDBCard color="indigo" text="white" className="text-center" align="center" style={{ maxWidth: "22rem" }}>
                    <MDBCardBody align = "center" >
                            FILL IN THE DETAILS
                    </MDBCardBody>
                </MDBCard> 
                </MDBCol>   
                </MDBRow>   
                <form onSubmit={this.handleSubmit}>

                <MDBRow center>
                    <MDBCol md="6" className="mb-4">
                    
                    <label> Source </label>
    
                    <PlacesAutocomplete
                    value={this.state.source}
                    onChange={this.handleSourceAddressChange}
                    >
                    {({ getInputProps, suggestions, getSuggestionItemProps }) => {
                    return (
                        <div className="Demo__search-bar-container">
                        <div className="Demo__search-input-container">
                            <input
                            {...getInputProps({
                                placeholder: "Tag the location",
                                className: "Demo__search-input"
                            })}
                            />
                            {this.state.source.length > 0 && (
                            <button
                                className="Demo__clear-button"
                                onClick={this.handleCloseClick}
                            >
                                x
                            </button>
                            )}
                        </div>
                        {suggestions.length > 0 && (
                            <div className="Demo__autocomplete-container">
                            {suggestions.map(suggestion => {
                                const className = classnames("Demo__suggestion-item", {
                                "Demo__suggestion-item--active": suggestion.active
                                });

                                return (
                                /* eslint-disable react/jsx-key */
                                <div
                                    {...getSuggestionItemProps(suggestion, {
                                    className
                                    })}
                                >
                                    <strong>
                                    {suggestion.formattedSuggestion.mainText}
                                    </strong>{" "}
                                    <small>
                                    {suggestion.formattedSuggestion.secondaryText}
                                    </small>
                                </div>
                                );
                                /* eslint-enable react/jsx-key */
                            })}
                            <div className="Demo__dropdown-footer">
                                <div>
                                <img
                                    src="http://files.hostgator.co.in/hostgator254362/image/powered-by-google.png"
                                    className="Demo__dropdown-footer-image"
                                />
                                </div>
                            </div>
                            </div>
                        )}
                        </div>
                    );
                    }}
                </PlacesAutocomplete>
                </MDBCol>
                </MDBRow>
                <MDBRow center>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="SOURCE"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.source}
                style={{ maxWidth: "18rem" }}
                //icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow>                  
                <MDBRow>
                    <MDBCol md="6" className="mb-4">
                    
                    <label> Destination </label>
    
                    <PlacesAutocomplete
                    value={this.state.destination}
                    onChange={this.handleDestinationAddressChange}
                    >
                    {({ getInputProps, suggestions, getSuggestionItemProps }) => {
                    return (
                        <div className="Demo__search-bar-container">
                        <div className="Demo__search-input-container">
                            <input
                            {...getInputProps({
                                placeholder: "Tag the location",
                                className: "Demo__search-input"
                            })}
                            />
                            {this.state.destination.length > 0 && (
                            <button
                                className="Demo__clear-button"
                                onClick={this.handleCloseClick}
                            >
                                x
                            </button>
                            )}
                        </div>
                        {suggestions.length > 0 && (
                            <div className="Demo__autocomplete-container">
                            {suggestions.map(suggestion => {
                                const className = classnames("Demo__suggestion-item", {
                                "Demo__suggestion-item--active": suggestion.active
                                });

                                return (
                                /* eslint-disable react/jsx-key */
                                <div
                                    {...getSuggestionItemProps(suggestion, {
                                    className
                                    })}
                                >
                                    <strong>
                                    {suggestion.formattedSuggestion.mainText}
                                    </strong>{" "}
                                    <small>
                                    {suggestion.formattedSuggestion.secondaryText}
                                    </small>
                                </div>
                                );
                                /* eslint-enable react/jsx-key */
                            })}
                            <div className="Demo__dropdown-footer">
                                <div>
                                <img
                                    src="http://files.hostgator.co.in/hostgator254362/image/powered-by-google.png"
                                    className="Demo__dropdown-footer-image"
                                />
                                </div>
                            </div>
                            </div>
                        )}
                        </div>
                    );
                    }}
                </PlacesAutocomplete>
                </MDBCol>
                </MDBRow>
                    
                 <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="DESTINATION"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.destination}
                style={{ maxWidth: "18rem" }}
                //icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow>          
                 <MDBRow>
                <MDBCol md="4" className="mb-4">
                <label> TimeofJourney </label>  
                <TimeField
                    value = {this.state.time} onChange={this.handletimechange}
                    //value={time}                     // {String}   required, format '00:00' or '00:00:00'
                    //onChange={(value) => {...}}      // {Function} required
                    input={<input type="text" />} // {Element}  default: <input type="text" />
                    colon=":"                        // {String}   default: ":"
                    showSeconds                      // {Boolean}  default: false
                />                                 
                {/* <input
                type="time"
                step="1"
                value = {this.state.time} onChange={this.handletimechange}
                //value={this.state.time}
                className="form-control"
                placeholder="Time"
               // onChange={(ev) => {this.setState({time:ev.target.value})}
                /> */}
                </MDBCol>
                </MDBRow>                  
                <br/>
                <br/>                                                         
                <MDBRow>  
                <MDBCol md="4" className="mb-4">                   
                                
                <label> DateofJourney </label>    
                <input type = "date" value = {this.state.date} onChange={this.handledatechange} />  {/*(onChange = {event => this.setState({date:event.target.value})} required*/}
                </MDBCol>
                </MDBRow> 
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="Price per co traveller"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                
                value = {this.state.ppt} onChange={this.handlepptchange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      

                </MDBCol>
                </MDBRow>
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="Number of Seats"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.nos} onChange={this.handlenoschange}
                //value = {this.state.lastname} onChange={this.handlelastnamechange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow> 
                <MDBRow>
                <MDBCol md="6" className="mb-4">
                <MDBInput
                label="anything to say"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.ats} onChange={this.handleatschange}
                style={{ maxWidth: "18rem" }}
                icon="envelope"
                required
                />      
                </MDBCol>
                </MDBRow>                                                              
                    <MDBRow>  
                    <MDBCol md="6" className="mb-4">                   
                    <Fragment>

                    <MDBBtn gradient="blue" type="submit">Submit</MDBBtn>
                    </Fragment>  
                    </MDBCol>
                </MDBRow>                        
                
        </form>  
        
        
        </MDBContainer>                       
            </div>
        );
    }
}

export default Offer;
