import React ,{ Component } from "react";
import Toper2 from './Toper2';
import axios from 'axios';
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBModalFooter,
  MDBIcon,
  MDBCardHeader,
  MDBBtn,
  MDBInput
} from "mdbreact";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            
            info : [],
            email : '',
            password : '',
            result : 'not logged in'
        }   
        }
        handleresultchange(props){
            if(props)
            {
                this.setState({result : "logged in" });
            }
            else{
                this.setState({result : "wrong credentials" });
            }
        }          
        handleemailchange = (event) => {
            this.setState({
                email : event.target.value
            })
        }   
        handlepasswordchange = (event) => {
            this.setState({
                password : event.target.value
            })
        } 
        display(){
            console.log("done");

            if (this.state.info.indexOf(this.state.email)==-1) {
                this.handleresultchange(false);
            }
            else {
                if(this.state.info[this.state.info.indexOf(this.state.email)].password==this.state.password)
                {
                    console.log("done");
                    console.log("done");
                    console.log("done");
                    console.log("done");
                    this.handleresultchange(true);
                }
                else
                {
                    console.log("not done");
                    console.log("not done");
                    console.log("not done");
                    console.log("not done");                   
                    this.handleresultchange(false);
                }
            }
        }             
        handlesubmit = (event) => {
            axios.get('http://127.0.0.1:8000/api/')
            .then(res=>{
                this.setState({
                    info : res.data
                });
                console.log(res.data);
            })
            //console.log("done");
            this.display();
        }
                          
render() {
  return (
     <div>
          <Toper2/> 
    <MDBContainer>
     
        
      <MDBRow>
        <MDBCol md="6">
          <MDBCard>
            <MDBCardBody>
              <MDBCardHeader className="form-header deep-blue-gradient rounded">
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> Login:
                </h3>
              </MDBCardHeader>
              <form onSubmit={this.handlesubmit}>
                <div className="grey-text">
                  <MDBInput
                    label="Type your email"
                    icon="envelope"
                    group
                    type="email"
                    validate
                    error="wrong"
                    success="right"
                    value = {this.state.email} onChange={this.handleemailchange}
                  />
                  <MDBInput
                    label="Type your password"
                    icon="lock"
                    group
                    type="password"
                    validate
                    value = {this.state.password} onChange={this.handlepasswordchange}
                  />
                </div>

              <div className="text-center mt-4">
                <MDBBtn
                  color="light-blue"
                  className="mb-3"
                  type="submit"
                >
                  Login
                </MDBBtn>
              </div>
              </form>
              <div>
                  {this.state.result}
                  </div>
              <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Not a member? Sign Up</p>
                  <p>Forgot Password?</p>
                </div>
              </MDBModalFooter>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
    </div>
  );
}
}
export default Login;
